mapper.select:
SELECT COUNT(*) FROM
PRODUCTION
WHERE CATE_CODE = #{cate_code}

SELECT A.PRO_NUM,A.TITLE,A.PRICE,A.CATE_CODE,A.STATE_MSG,A.PLACE_SIGNAL,A.CREATE_DATE,A.ADDR,A.PLACE_PICK,B.UUID,B.UPLOADPATH,B.FILENAME,B.REP FROM (SELECT PRO_NUM,TITLE,PRICE,CATE_CODE,STATE_MSG,PLACE_SIGNAL,CREATE_DATE,ADDR,PLACE_PICK FROM PRODUCTION PRODUCTION WHERE CATE_CODE = #{cate_code}
) A , PRODUCTION_UPLOAD B WHERE A.PRO_NUM = B.PRO_NUM AND B.REP = 1
ORDER BY A.PRICE ASC
ORDER BY A.PRICE DESC
